var images = [
		"0du/img/1.jpg",
		"0du/img/2.jpg",
		"0du/img/3.jpg",
		"0du/img/4.jpg",
		"0du/img/5.jpg"
	];
	$(images).each(function(){
	   $('<img/>')[0].src = this; 
	});
	var index = 0;
	$.backstretch(images[index], {speed: 800});
	setInterval(function() {
		index = (index >= images.length - 1) ? 0 : index + 1;
		$.backstretch(images[index]);
	}, 6000);